}"},{"col_type":"movie_3","name":"搜索解析","path":"sp","rule":"function search(url, key, rule) {
    let d = [];
    let html = "";
    if (url.includes("**")) {
        //get请求
        let old = url;
        url = url.replace("**", key);
        html = request(url);
        //自动过验证码和频繁访问限制
        let {
            check, test
        } = $.require("hiker://page/yzm");
        if (check(url, html, d)) {
            log("重试了");
            html = request(url, {
                headers: {
                    "Referer": url
                }
            });           
        }         
        test(url, html, d, old, key, rule);
    } else {
        //自动识别post请求
        html = request(url);
        let form = pdfh(html, "body&&form&&Html");
        let action = pd(html, "form&&action", url);
        let inputs = pdfa(html, "body&&form&&input");
        let body = {};
        for (let it of inputs) {
            let name = pdfh(it, "input&&name");
            if (name && name != "") {
                let value = pdfh(it, "input&&value");
                body[name] = value || key;
            }
        }
        html = post(action, {
            body: body
        });
    }

    let alist = pdfa(html, "body&&a");
    let arr = alist.map(it => {
        let t = pdfh(it, "a&&Text");
        let t2 = pdfh(it, "a&&title");
        return {
            //html: it,
            text: t2 && t2.includes(key) ? t2 : t,
            href: pd(it, "a&&href", url)
        }
    });

    function isResult(it) {
        it = it.text || "";
        if (it == "" || it.match(/搜索|结果/)) {
            return false
        }
        return it.includes(key);
    }

    for (let i = 0; i < arr.length; i++) {
        let it = arr[i];
        let t = it.text;
        if (!it.href || it.href == "") {
            continue;
        }
        if (isResult(it)) {
            d.push({
                title: t,
                url: "hiker://page/p?u=" + base64Encode(it.href),
                col_type: "text_1",
                desc: "““””<font color=#f13b66a>" + rule + "</font>",
                extra: {
                    inheritTitle: false
                }
            });
        }
    }
    return d;
}
