        if (!file.exists()) {
            return null;
        }
        let fileInputStream = new FileInputStream(file);
        let byteArrayOutputStream = new ByteArrayOutputStream(1024);
        //let bArr = new byte[1024];
        let bArr = java.lang.reflect.Array.newInstance(java.lang.Byte.TYPE, 1024);
        while (true) {
            let read = fileInputStream.read(bArr);
            if (read != -1) {
                byteArrayOutputStream.write(bArr, 0, read);
            } else {
                fileInputStream.close();
                byteArrayOutputStream.close();
                return byteArrayOutputStream.toByteArray();
            }
        }
    } catch (e) {
        //e.printStackTrace();
        log(e.toString());
        return null;
    }
}

function byteToBitmap(imgByte, inSampleSize) {
    let options = new BitmapFactory.Options();
    options.inSampleSize = inSampleSize;
    options.inPreferredConfig = Bitmap.Config.RGB_565;
    options.inJustDecodeBounds = false;
    options.inPurgeable = true;
    options.inInputShareable = true;
    let byteArrayInputStream = new ByteArrayInputStream(imgByte);
    let bitmap = new SoftReference(BitmapFactory.decodeStream(byteArrayInputStream, null, options)).get();
    try {
        byteArrayInputStream.close();
    } catch (e) {
        log(e.toString());
    }
    return bitmap;
}
function bitmap2Path(bitmap, path) {
    try {
        let os = new FileOutputStream(path);
        bitmap.compress(Bitmap.CompressFormat.PNG, 100, os);
        os.flush();
        os.close();
    } catch (e) {
        log(e.toString())
    }
    return path;
}
