function getReferer(url) {
    if (url.indexOf("gitee") > 0) {
        return "https://" + url.split("://")[1].split("/")[0];
    } else {
        return null
    }
}

function renderLiveData(url) {
    try {
        var txts = request(url, {
                headers: {
                    Referer: getReferer(url)
                }
            }).replace(/#EXTINF:-1 ,/g, '')
            .replace(/#EXTINF:-1,/g, '')
            .replace(/\n#genre#/g, ',#genre#')
            .replace(/#EXTINF:-1/g, '')
            .replace(/group-title="/g, '')
            .replace(/\nhttp/g, ',http')
            .replace(/=/g, '')
            .replace(/，#/g, ',#')
            .replace(/",/g, '/')
            .replace(/ /g, '')
            .split('\n');
        const d1 = []
        for (var i = 0; i < txts.length; i++) {
            var r = {};
            var j = txts[i].split(',');
            if (txts[i].indexOf("#EXTINF") == 0 &&
                j.length == 3 && j[2].indexOf("http") == 0) {
                r.title = j[1].split('/')[j[0].split('/').length - 1];
                r.url = j[2].replace(`\n`, '').replace('\r', '');
                if (r.url.indexOf("http") == 0) {
                    r.url = r.url + "#isVideo=true#";
                }
                d1.push(r);
                continue;
            }

            if (j.length < 2) {
                continue;
            }
            r.title = j[0].split('/')[j[0].split('/').length - 1];
            r.url = j[1].replace(`\n`, '').replace('\r', '');
            if (r.url.indexOf("http") == 0) {
                r.url = r.url + "#isVideo=true#";
            }
            d1.push(r);
        }
        //多线路
        const d2 = {}
        for (let it of d1) {
            if (d2[it.title] == null) {
                d2[it.title] = []
            }
            d2[it.title].push(it.url)
        }

        for (let it of Object.keys(d2)) {
            data.push({
                title: it,
                url: JSON.stringify({
                    urls: d2[it]
                })
            })
        }
    } catch (e) {
        data.push({
            title: "数据加载失败",
            desc: JSON.stringify(e),
            col_type: "text_1"
        })
    }
}

if (config == "" || MY_URL.indexOf('setting') > 0) {
    data = renderSetting();
} else {
    config = getConfig();
    if (config.data.length <= 0) {
        data = renderSetting();
    } else {
        let select = getVar("live-select", config.data[0].name);
        log(select)
        let has = config.data.map(i => i.name).includes(select);
        log(has)
        if (!has) {
            select = config.data[0].name;
        }
        data.push({
            col_type: "big_blank_block"
        })
        for (source of config.data) {
            data.push({
                title: source.name == select ? "““””<span style='color: #1aad19'>" + select + "</span>" : source.name,
                col_type: "scroll_button",
                url: $().lazyRule((a) => {
                    putVar("live-select", a);
                    refreshPage();
                    return "hiker://empty"
                }, source.name)
            })
        }

        for (source of config.data) {
            if (source.name == select) {
                renderLiveData(source.url);
                break
            }
        }
    }
}
setResult(data);

